        {"label": "String", "value": "string"},
        {"label": "Number", "value": "number"},
        {"label": "Boolean", "value": "boolean"},
        {"label": "Float", "value": "float"},
        {"label": "Object", "value": "object"}
      ]
    },
    {
      "type": "fillIn",
      "question": "Complete the code: const greeting = \"Hello, \" + ___;",
      "answer": "name"
    }
  ]
}

